//Action Type
const  CREATE = "haeDal/CREATE";
const  LOAD = "haeDal/LOAD";
const  DELETE = "haeDal/DELETE";
const  UPDATE = "haeDal/UPDATE";

const initialState = {list: [{happyPoint : "5",  img : "이미지", text : "글"}

]};

//Action creator
//todo: text 업로드
export const createText = (data) => {
    return {type: CREATE, data};
};




//Reducer
export default function reducer(state = initialState, action ={}) {
switch(action.type) {
    //todo: text 업로드
    case "haeDal/CREATE": {
        const new_content= [...state.list, action.data]
        return {...state, list: new_content};
}

        default: return state;
    }
}
