const MypageWrap = styled.div`
	.ranking_info {
    padding-top: 60px;
    .ranking {
		font-size: 4rem;
		font-weight: 300;
		span {
			font-weight: 500;
		}}
	}
`;
const SectionTitle = styled.h3`
	font-size: 2.4rem;
	padding-bottom: 20px;
	margin-bottom: 50px;
	border-bottom: 1px solid #000;
	font-weight: 300;
`;
const SectionSubTitle = styled.p`
	position: relative;
	font-size: 2rem;
	font-weight: 700;
	margin-bottom: 20px;
	span {
		position: relative;
		display: inline-block;
		&:before {
			content: "";
			display: block;
			position: absolute;
			left: -4px;
