]};

//Action creator
//todo: text 업로드
export const createText = (data) => {
    // console.log(data)
    return {type: CREATE, data};
};




//Reducer
export default function reducer(state = initialState, action ={}) {
switch(action.type) {
    //todo: text 업로드
